<script lang="ts">
	import { CurrentRoutePointStore } from '$lib/stores';
	import { onMount } from 'svelte';
	import FrequencyDial from './FrequencyDial.svelte';

	let mounted: boolean = false;
	let currentAltitude: number = 0;
	let currentPressure: number = 1005 - Math.random() * 10;

	CurrentRoutePointStore.subscribe((currentRoutePoint) => {
		if (currentRoutePoint != null && mounted) {
			let targetPose = currentRoutePoint.pose;
			currentAltitude = targetPose.altitude;
		}
	});

	$: if (mounted) {
		setAltitudeDial(currentAltitude);
		if (currentPressure < 980) currentPressure = 980;
		if (currentPressure > 1035) currentPressure = 1035;
		setPressureDial(currentPressure);
	}

	function setAltitudeDial(altitude: number) {
		const longArrow = document.getElementsByClassName('long-arrow')[0] as HTMLAnchorElement;
		const shortArrow = document.getElementsByClassName('short-arrow')[0] as HTMLAnchorElement;
		if (longArrow != null) {
			var newRotation = Math.round((altitude / 1000) * 360) + 90;
			longArrow.style.transform = 'rotate(' + newRotation + 'deg)';
		}
		if (shortArrow != null) {
			var newRotation = Math.round((altitude / 10000) * 360) + 90;
			shortArrow.style.transform = 'rotate(' + newRotation + 'deg) scaleX(0.7) scaleY(1.3)';
		}
	}

	function setPressureDial(pressure: number) {
		const dialFace = document.getElementsByClassName('altitudepressure')[0] as HTMLAnchorElement;
		if (dialFace != null) {
			// 90 degrees is 1035 (max), -20 degrees is 980 (min), correct with - 540
			var newRotation = Math.round(((pressure % 360) / 55) * 110) - 540;
			dialFace.style.transform = 'rotate(' + newRotation + 'deg)';
		}
	}

	function onIncrementSideTurn() {
		currentPressure += 1;
	}

	function onDecrementSideTurn() {
		currentPressure -= 1;
	}

	onMount(() => {
		mounted = true;
	});

	// Remeber to credit Peter Shmulevich: https://codepen.io/pshmulevich/pen/YMxbNv
</script>

